inSequence.script:
var payload = mc.getPayloadJSON();
var user = mc.getProperty('userProperty');
print(user);

var Reviews;

if (payload === null || typeof payload === "undefined" || payload === "") {
    Reviews = 0;
} else if (Array.isArray(payload)) {
    var filteredPayload = payload.filter(function(item) {
        return item.userId == user;
    });
    Reviews = filteredPayload.length;
} else {
    // Handle the case when payload is not an array
    Reviews = 0;
}

var responsePayload = {
	Total_Number_of_Reviews: Reviews,
};
// Set the response payload
mc.setPayloadJSON(responsePayload);

case.script:
//print(JSON.stringify(mc.getPayloadJSON()));
                                        var payload = mc.getPayloadJSON();
                                        var percentage = mc.getProperty('percentageProperty');
                                        var filteredPayload = payload.filter(function(item) {
                                            if((item.downVotes / (item.downVotes + item.vote)) * 100 > percentage){
                                                return true;
                                            }
                                            return false;
                                        }); 

                                        //mc.setPayloadJSON(filteredPayload);
                                        mc.setPayloadJSON(filteredPayload);
                                        print("filteredPayload -->"+filteredPayload.length);
                                        if(filteredPayload.length > 0) {
                                            mc.setPayloadJSON({result: "true"});
                                        }
                                        else {
                                            mc.setPayloadJSON({result: "false"});
                                        }

